$(document).on( "click", '.slds-checkbox-button input', function() {
  $(this).parents('.slds-checkbox-button').toggleClass('slds-checkbox-button_is-checked');
  $(this).parents('.slds-checkbox-button').toggleClass('slds-is-selected');
  if($(this).parents('.slds-checkbox-button').hasClass('slds-checkbox-button_is-checked')==true){
    $(this).parents('.slds-checkbox-button').find('use').attr('xlink:href','/assets/icons/utility-sprite/svg/symbols.svg#check')
  } else {
    $(this).parents('.slds-checkbox-button').find('use').attr('xlink:href','/assets/icons/utility-sprite/svg/symbols.svg#add')
  }
});