function dataTable(data) {
  var fields = Object.keys(data[0]);
  var html = '<table class="slds-table slds-table_cell-buffer slds-table_bordered">';
  html += '  <thead>';
  html += '    <tr class="slds-line-height_reset">';
  for (i = 0; i < fields.length; i++) {
    html += '      <th class="" scope="col">';
    html += '        <div class="slds-truncate" title="'+ fields[i] + '">'+ fields[i] + '</div>';
    html += '      </th>';
  }
  html += '    </tr>';
  html += '  </thead>';
  html += '  <tbody>';
  for (j = 0; j < data.length; j++) {
    html += '    <tr class="slds-hint-parent">';
    for (i = 0; i < fields.length; i++) {
      html += '      <td data-label="'+ fields[i] + '">';
      html += '        <div class="slds-truncate" title="'+ fields[i] + '">'+ data[j][fields[i]] + '</div>';
      html += '      </td>';
    }
    html += '    </tr>';
  }
  html += '  </tbody>';
  html += '</table>';
return html;
}