function dataTable(data) {
  var fields = Object.keys(data[0]);
  var html = '<table class="slds-table slds-table_cell-buffer slds-table_bordered slds-table_fixed-layout slds-size_1-of-1">';
  html += '  <thead>';
  html += '    <tr class="slds-line-height_reset">';
  for (f = 0; f < fields.length; f++) {
    html += '      <th class="" scope="col">';
    html += '        <div class="slds-truncate" title="'+ fields[f] + '">'+ fields[f] + '</div>';
    html += '      </th>';
  }
  html += '    </tr>';
  html += '  </thead>';
  html += '  <tbody>';
  for (r = 0; r < data.length; r++) {
    html += '    <tr class="slds-hint-parent">';
    for (f = 0; f < fields.length; f++) {
      html += '      <td data-label="'+ fields[f] + '">';
      html += '        <div class="slds-truncate" title="'+ fields[f] + '">'+ data[r][fields[f]] + '</div>';
      html += '      </td>';
    }
    html += '    </tr>';
  }
  html += '  </tbody>';
  html += '</table>';
return html;
}