!function(s,n,o){
  s(".slds-popover .slds-popover__close").on("click",function(){
    s(this).parents(".slds-popover").addClass("slds-hide")
  })
  s('.slds-button_icon[aria-describedby="help"]').on("click",function(){
    s(this).parents(".slds-form-element__icon").find('.slds-popover_tooltip').toggleClass("slds-hide")
  })
}(jQuery,window,document);

$('.slds-button_icon[aria-describedby="help"]').hover(
  function() {
    $(this).parents(".slds-form-element__icon").find('.slds-popover_tooltip').removeClass("slds-hide");
  }, function() {
    $(this).parents(".slds-form-element__icon").find('.slds-popover_tooltip').addClass("slds-hide");
  }
);