function progress(max, current) {
  if(current >= max){var perc = 100; var color = '#4bca81';}
  else {var perc = parseInt((current*100)/(max-1)); var color = '#5eb4ff';}
  var html = '';
  html += '<div class="slds-progress">';
  html += '  <ol class="slds-progress__list">';
  for (i = 0; i < max; i++) {
    if(i == max - 1 && current >= max){
      html += '    <li class="slds-progress__item slds-is-completed">';
      html += '      <button class="slds-button slds-button_icon slds-progress__marker slds-progress__marker_icon" title="Step ' + (i + 1) + ' - Completed" style="color:' + color + ';">';
      html += '        <svg class="slds-button__icon slds-button__icon_large" aria-hidden="true">';
      html += '          <use xlink:href="/assets/icons/utility-sprite/svg/symbols.svg#success"></use>';
      html += '        </svg>';
      html += '        <span class="slds-assistive-text">Step '+ (i+1) +'</span>';
      html += '      </button>';
      html += '    </li>';
    } else if(i<current){
        html += '    <li class="slds-progress__item slds-is-completed">';
        html += '      <button class="slds-button slds-button_icon slds-progress__marker slds-progress__marker_icon" title="Step ' + (i + 1) + ' - Completed" style="color:' + color + ';">';
        html += '        <svg class="slds-button__icon" aria-hidden="true">';
        html += '          <use xlink:href="/assets/icons/utility-sprite/svg/symbols.svg#success"></use>';
        html += '        </svg>';
        html += '        <span class="slds-assistive-text">Step '+ (i+1) +'</span>';
        html += '      </button>';
        html += '    </li>';
    } else if(i==current){
      html += '    <li class="slds-progress__item slds-is-active">';
      html += '      <button class="slds-button slds-progress__marker">';
      html += '        <span class="slds-assistive-text">Step '+ (i+1) +'</span>';
      html += '      </button>';
      html += '    </li>';
    } else {
      html += '    <li class="slds-progress__item">';
      html += '      <button class="slds-button slds-progress__marker">';
      html += '        <span class="slds-assistive-text">Step '+ (i+1) +'</span>';
      html += '      </button>';
      html += '    </li>';
    }
  }
  html += '  </ol>';
  html += '  <div class="slds-progress-bar slds-progress-bar_x-small" aria-valuemin="0" aria-valuemax="'+max+'" aria-valuenow="'+current+'" role="progressbar">';
  html += '    <span class="slds-progress-bar__value" style="width:' + perc + '%; background:' + color + ';">';
  html += '      <span class="slds-assistive-text">Progress: ' + perc + '%</span>';
  html += '    </span>';
  html += '  </div>';
  html += '</div>';
  return html
}