function setupStep(input){
  var htm = '';
  var type = '';
  htm +='<article class="slds-setup-assistant__step">';
  htm +='  <div class="slds-setup-assistant__step-summary">';
  htm +='    <div class="slds-media">';

  if(input.type == 'complete'){
    htm +='      <div class="slds-media__figure">';
    htm +='        <div class="slds-progress-ring slds-progress-ring_complete slds-progress-ring_large">';
    htm +='          <div class="slds-progress-ring__content">';
    htm +='            <span class="slds-icon_container slds-icon-utility-check" title="Complete">';
    htm +='              <svg class="slds-icon" aria-hidden="true">';
    htm +='                <use xlink:href="/assets/icons/utility-sprite/svg/symbols.svg#check"></use>';
    htm +='              </svg>';
    htm +='              <span class="slds-assistive-text">Complete</span>';
    htm +='            </span>';
    htm +='          </div>';
    htm +='        </div>';
    htm +='      </div>';
  }
  if(input.type == 'error'){
    htm +='<div class="slds-media__figure slds-align_absolute-center">';
    htm +='  <span class="slds-icon_container slds-icon_container_circle slds-icon-action-close" title="Description of icon when needed">';
    htm +='    <svg class="slds-icon slds-icon_x-small" aria-hidden="true">';
    htm +='      <use xlink:href="/assets/icons/action-sprite/svg/symbols.svg#reject"></use>';
    htm +='    </svg>';
    htm +='    <span class="slds-assistive-text">Description of icon when needed</span>';
    htm +='  </span>';
    htm +='</div>';
  }
  if(input.type == 'info'){
    htm +='<div class="slds-media__figure slds-align_absolute-center">';
    htm +='  <span class="slds-icon_container slds-icon_container_circle slds-icon-action-script" title="Description of icon when needed">';
    htm +='    <svg class="slds-icon slds-icon_x-small" aria-hidden="true">';
    htm +='      <use xlink:href="/assets/icons/action-sprite/svg/symbols.svg#priority"></use>';
    htm +='    </svg>';
    htm +='    <span class="slds-assistive-text">Description of icon when needed</span>';
    htm +='  </span>';
    htm +='</div>';
  }
  htm +='      <div class="slds-media__body slds-m-top_x-small">';
  htm +='        <div class="slds-media">';
  htm +='          <div class="slds-setup-assistant__step-summary-content slds-media__body slds-text-align_left">';
  htm +='            <h3 class="slds-setup-assistant__step-summary-title slds-text-heading_small">' + input.title + '</h3>';
  htm +='            <p>' + input.body + '</p>';
  htm +='          </div>';
  htm +='        </div>';
  htm +='      </div>';
  htm +='    </div>';
  htm +='  </div>';
  htm +='</article> ';
  return htm
}