function toast(theme, icon, message) {
  $('.slds-notify_container').remove()
  var html = '';
  html += '<div class="slds-notify_container">';
  html += '  <div class="slds-notify slds-notify_toast slds-theme_' + theme + '" role="status">';
  html += '    <span class="slds-assistive-text">warning</span>';
  html += '    <span class="slds-icon_container slds-icon-utility-warning slds-m-right_small slds-no-flex slds-align-top" title="Description of icon when needed">';
  html += '      <svg class="slds-icon slds-icon_small" aria-hidden="true">';
  html += '        <use xlink:href="/assets/icons/utility-sprite/svg/symbols.svg#' + icon + '"></use>';
  html += '      </svg>';
  html += '    </span>';
  html += '    <div class="slds-notify__content slds-text-title slds-text-color_inverse" style="font-size: 0.875rem !important;">' + message + '</div>';
  html += '    <div class="slds-notify__close">';
  html += '      <button class="slds-button slds-button_icon slds-button_icon-inverse" title="Close">';
  html += '        <svg class="slds-button__icon slds-button__icon_large" aria-hidden="true">';
  html += '          <use xlink:href="/assets/icons/utility-sprite/svg/symbols.svg#close"></use>';
  html += '        </svg>';
  html += '        <span class="slds-assistive-text">Close</span>';
  html += '      </button>';
  html += '    </div>';
  html += '  </div>';
  html += '</div>';
  return html
}
$(document).on("click",".slds-notify__close",function() {
  $(this).parents('.slds-notify_container').remove()
});