$(document).on("mouseenter", ".slds-form-element__icon .slds-button_icon", function() {
  var tip = tooltips[$(this).attr('data-message-id')];
  var tootip = '<div class="slds-popover slds-popover_tooltip ' + tip.layout + ' role="tooltip" style="position:absolute;top:' + tip.top + ';left:' + tip.left + ';width:' + tip.width + ';line-height:1.25">';
  tootip += '<div class="slds-popover__body">' + tip.msg + '</div>'
  tootip += '</div>'
  $(this).after(tootip);
});

$(document).on("mouseleave", ".slds-form-element__icon .slds-button_icon", function() {
  $(this).parent().find('.slds-popover_tooltip').remove();
});